(191, 1, '2021-08-18 09:09:38', '2021-08-18 09:09:38', '<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"fr\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Connexion</title>\n</head>\n<body>\n    <form action=\"\" method=\"POST\">\n        <fieldset> \n            <legend>\n                ajout de document\n            </legend> \n            <div>\n                <label for=\"ufr\">UFR</label>\n                <select name=\"ufr\" id=\"ufr\">\n                    <option value=\"sfi\"> SFI </option>\n                    <option value=\"sejtses\"> SES </option>\n                    <option value=\"ses\"> SEJT </option>\n                    <option value=\"seapan\"> SEAPAN </option>\n               </select> <br>\n\n               <label for=\"departement\">Departement</label>\n               <select name=\"departement\" id=\"departement\">\n                    <option value=\"mi\"> Math Informatique </option>\n                    <option value=\"sta\"> Sciences et Technologies Alimentaires </option>\n                    <option value=\"hgme\"> Hydraulique, Genie rural, Machinisme et Energies renouvelables </option>\n                    <option value=\"segc\"> Sciences économiques, Gestion et Commerce </option>\n                    <option value=\"sjp\"> Sciences juridiques et politiques </option>\n                    <option value=\"thgr\"> Tourisme, Hôtellerie, Gastronomie et Restauration </option>\n                    <option value=\"edb\"> Environnement, Biodiversité et Développemnt durable </option>\n                    <option value=\"ss\"> Sciences sociales </option>\n                    <option value=\"apg\">Agronomie et Production Végétale</option>\n                    <option value=\"ste\"> Sciences et Techniques d’Elevage  </option>\n                    <option value=\"grhpa\"> Gestion des Ressources Halieutiques, Pêche et Aquaculture </option>\n                    <option value=\"al\">Alimentation et Nutrition </option>\n              </select> <br>\n\n                <label for=\"licence\">Licence</label>\n                <input type=\"text\" name=\"licence\" id=\"licence\"> <br>\n\n                <label for=\"niveau\">niveau</label>\n                <select name=\"niveau\" id=\"niveau\">\n                    <option value=\"l1\">licence1</option>\n                    <option value=\"l2\">licence2</option>\n                    <option value=\"l3\">licence3</option>                 \n                </select><br>\n\n                <label for=\"nature\">Nature</label>\n                <select name=\"nature\" id=\"nature\">\n                    <option value=\"td\">TD</option>\n                    <option value=\"devoir\">Devoir</option>\n                    <option value=\"examen\">examen</option>\n                    <option value=\"correction_td\">Correction TD </option>\n                    <option value=\"correction_devoir\">Correction devoir </option>\n                    <option value=\"correction_examen\">Correction examen </option>                   \n                </select><br>\n\n                <label for=\"matiere\">Matière</label>\n                <input type=\"text\" name=\"matiere\" id=\"matiere\"><br>\n\n                <label for=\"date\">Annnée</label>\n                <input type=\"date\" name=\"date\" id=\"date\"><br>\n\n                <label for=\"document\">document</label>\n                <input type=\"file\"   name=\"document\" id=\"document\"><br>\n                \n\n\n                \n                <input type=\"submit\" value=\"se connecter\">\n            </div>\n        </fieldset>\n\n    </form>\n  \n       \n        \n</body>\n</html>\n<!-- /wp:html -->', 'Ajout de documents', '', 'inherit', 'closed', 'closed', '', '181-revision-v1', '', '', '2021-08-18 09:09:38', '2021-08-18 09:09:38', '', 181, 'http://localhost/samadoc/?p=191', 0, 'revision', '', 0),
